import React, { useState, useEffect } from 'react';
import styles from './styles.module.css';

const Pomodoro = () => {
  const [mode, setMode] = useState('pomodoro');
  const [minutes, setMinutes] = useState(25);
  const [seconds, setSeconds] = useState(0);
  const [isActive, setIsActive] = useState(false);
  const [progress, setProgress] = useState(0);

  useEffect(() => {
    let interval = null;

    if (isActive) {
      interval = setInterval(() => {
        if (seconds === 0) {
          if (minutes === 0) {
            clearInterval(interval);
            setIsActive(false);
          } else {
            setMinutes(minutes - 1);
            setSeconds(59);
          }
        } else {
          setSeconds(seconds - 1);
        }
        setProgress((prevProgress) => prevProgress + 1 / ((minutes * 60 + seconds) || 1) * 100);
      }, 1000);
    } else if (!isActive && seconds !== 0) {
      clearInterval(interval);
    }

    return () => clearInterval(interval);
  }, [isActive, minutes, seconds]);

  const handleModeChange = (newMode) => {
    setMode(newMode);
    switch (newMode) {
      case 'pomodoro':
        setMinutes(25);
        break;
      case 'shortBreak':
        setMinutes(5);
        break;
      case 'longBreak':
        setMinutes(15);
        break;
      default:
        break;
    }
    setSeconds(0);
    setIsActive(false);
    setProgress(0);
  };

  const toggleStartStop = () => {
    setIsActive(!isActive);
  };

  const resetTimer = () => {
    switch (mode) {
      case 'pomodoro':
        setMinutes(25);
        break;
      case 'shortBreak':
        setMinutes(5);
        break;
      case 'longBreak':
        setMinutes(15);
        break;
      default:
        break;
    }
    setSeconds(0);
    setIsActive(false);
    setProgress(0);
  };

  return (
    <main className={styles.app}>
      <progress id="js-progress" value={progress} max="100"></progress>
      <div className={styles.progressBar}></div>
      <div className={styles.timer}>
        <div className={styles.buttonGroup} id="js-mode-buttons">
          <button
            data-mode="pomodoro"
            className={`${styles.button} ${styles.modeButton} ${mode === 'pomodoro' ? styles.active : ''}`}
            onClick={() => handleModeChange('pomodoro')}
            id="js-pomodoro"
          >
            Pomodoro
          </button>
          <button
            data-mode="shortBreak"
            className={`${styles.button} ${styles.modeButton} ${mode === 'shortBreak' ? styles.active : ''}`}
            onClick={() => handleModeChange('shortBreak')}
            id="js-short-break"
          >
            Short break
          </button>
          <button
            data-mode="longBreak"
            className={`${styles.button} ${styles.modeButton} ${mode === 'longBreak' ? styles.active : ''}`}
            onClick={() => handleModeChange('longBreak')}
            id="js-long-break"
          >
            Long break
          </button>
        </div>
        <div className={styles.clock} id="js-clock">
          <span id="js-minutes">{String(minutes).padStart(2, '0')}</span>
          <span className={styles.separator}>:</span>
          <span id="js-seconds">{String(seconds).padStart(2, '0')}</span>
        </div>
        <div className={styles.buttonGroup}>
          <button className={styles.mainButton} data-action="start" id="js-btn" onClick={toggleStartStop}>
            {isActive ? 'Stop' : 'Start'}
          </button>
          <button className={styles.mainButton} data-action="reset" id="reset-btn" onClick={resetTimer}>
            Reset
          </button>
        </div>
      </div>
    </main>
  );
};

export default Pomodoro;
